import * as jwt from 'jsonwebtoken';
export class JWTUtils {
  // Otras propiedades y métodos de la clase...
  decode(token, claveSecreta) {
    try {
      // Utilizar la clave secreta y la biblioteca 'jsonwebtoken' para decodificar el token
      const decodedToken = jwt.verify(token, claveSecreta);
      return decodedToken;
    } catch (error) {
      // Manejar errores de decodificación
      console.error('Error decoding token:', error.message);
      throw new Error('Invalid token');
    }
  }
}
